import { HashRouter as Router,Route,Link } from "react-router-dom"

import Film from "./pages/Film"
import Cinema from "./pages/Cinema"
import Mine from "./pages/Mine"

import API from "./network/API"
import ajax from "./axios/axios"
import './App.css';

class App extends Component{
  constructor(props){
    super(props);
      this.state = {
            tabnav:[
                {title:"电影",to:"/film"},
                {title:"影院",to:"cinema"},
                {title:"我的",to:"mine"}
            ]
      }
  }
  //   componentDidMount(){
  //       ajax(API.Download_Address)
  // }

    showIndex(index){

    }
  render(){
      return(
          <Router>
              <div className="App">
                  <div>
                      <Route path="/film" component={Film}/>
                      <Route path="/cinema" component={Cinema}/>
                      <Route path="/mine" component={Mine}/>
                  </div>
                  <div className="foot-list">
                      <ul>
                          {
                              this.state.tabnav.map((item,index)=>{
                                  return <li className="color" key={index} onClick={this.showIndex.bind(this,index)}>
                                            {item.title}
                                          </li>
                              })
                          }
                      </ul>
                     {/* <Link to="/film">电影</Link>
                      <Link to="/cinema">影院</Link>
                      <Link to="mine">我的</Link>*/}
                  </div>
              </div>
          </Router>
    )
  }
}

export default App;
