class Home extends Component{
    constructor(props){
        super(props)
    }
    render(){

        return(
            <div>
                {store.getState().cinemaReducer.money}
            </div>
        )
    }
}

class Cinema extends Component{
    constructor(props){
        super(props);
        this.state={
            title:"影院",
            money:1000
        }
    }
    addChange(){

        store.dispatch({
            type:'ADD'
        });
        console.log(store.getState().cinemaReducer.money);
    }
    delChange(){
        store.dispatch({
            type:'DEL'
        });
        console.log(store.getState().cinemaReducer.money);
    }
    componentDidMount(){
        store.subscribe(()=>{
            this.setState({
                money:store.getState().cinemaReducer.money
            });

        });
        // console.log( a instanceof Object );
    }

    render(){
        return(
            <div className="cinema">
                <Header title={this.state.title}/>
                <h1>影院页</h1>
                <button onClick={this.addChange.bind(this)}>ADD</button>------------------------
                <button onClick={this.delChange.bind(this)}>DEL</button>
                <Home/>
            </div>
        )
    }
    componentWillUnmount(){

    }
}
export default Cinema
